array (
'id' => 377,
'simple_item_id' => 140,
'title' => 'Metropolitendə işləmək üçün nə etmək lazımdır?',
'subtitle' => NULL,
'link' => NULL,
'medium_text1' => 'Bunun üçün "Karyera" bölməsinin "Elektron ərizə" yarımbölməsindəki anketi doldurub, göndərmək lazımdır. Anket tamamlandıqdan sonra ekranda yaşıl fonda təsdiq məlumatının çıxmasına diqqət edin. Bu, ərizənizin müvafiq bazaya avtomatik yerləşdirilməsi deməkdir. Namizədliyiniz uyğun gəldiyi təqdirdə sizinlə əlaqə saxlanılacaq və müsabiqəyə dəvət olunacaqsınız.',
'medium_text2' => NULL,
'medium_text3' => NULL,
'medium_text4' => NULL,
'text_field' => '&lt;p&gt;Bunun &amp;uuml;&amp;ccedil;&amp;uuml;n &amp;quot;Karyera&amp;quot; b&amp;ouml;lməsinin &amp;quot;Elektron ərizə&amp;quot; yarımb&amp;ouml;lməsindəki anketi doldurub, g&amp;ouml;ndərmək lazımdır. Anket tamamlandıqdan sonra ekranda yaşıl fonda təsdiq məlumatının &amp;ccedil;ıxmasına diqqət edin. Bu, ərizənizin m&amp;uuml;vafiq bazaya avtomatik yerləşdirilməsi deməkdir. Namizədliyiniz uyğun gəldiyi təqdirdə sizinlə əlaqə saxlanılacaq və m&amp;uuml;sabiqəyə dəvət olunacaqsınız.&lt;/p&gt;',
'locale' => 'az',
),
