422 => 
array (
'id' => 612,
'simple_item_id' => 300,
'title' => 'Xüsusi karantin rejimi ilə bağlı əlavə tədbirlər barədə Azərbaycan Respublikası Nazirlər Kabinetinin Qərarı',
'subtitle' => NULL,
'link' => NULL,
'medium_text1' => NULL,
'medium_text2' => NULL,
'medium_text3' => NULL,
'medium_text4' => NULL,
'text_field' => '&lt;h2&gt;X&amp;uuml;susi karantin rejimi ilə bağlı əlavə tədbirlər barədə Azərbaycan Respublikası Nazirlər Kabinetinin Qərarı&lt;/h2&gt;

&lt;p&gt;Azərbaycan Respublikası Nazirlər Kabinetinin 2020-ci il 12 sentyabr tarixli 336 n&amp;ouml;mrəli Qərarında dəyişiklik edilməsi haqqında &amp;ldquo;Sanitariya-epidemioloji salamatlıq haqqında&amp;rdquo; Azərbaycan Respublikası Qanununun 25-ci maddəsinə və Azərbaycan Respublikası Nazirlər Kabinetinin 2020-ci il 28 fevral tarixli 73-1 n&amp;ouml;mrəli Qərarı ilə təsdiq edilmiş &amp;ldquo;Yoluxucu, parazitar və k&amp;uuml;tləvi qeyri-yoluxucu xəstəliklərin əmələ gəlməsi, yaxud yayılması təhl&amp;uuml;kəsi yarandıqda karantin-təşkilat, profilaktika və digər zəruri tədbirlərin g&amp;ouml;r&amp;uuml;lməsi Qaydaları&amp;rdquo;na əsasən Azərbaycan Respublikasının Nazirlər Kabineti qərara alır: &amp;ldquo;X&amp;uuml;susi karantin rejimi ilə bağlı əlavə tədbirlər barədə&amp;rdquo; Azərbaycan Respublikası Nazirlər Kabinetinin 2020-ci il 12 sentyabr tarixli 336 n&amp;ouml;mrəli Qərarının (Azərbaycan Respublikasının Qanunvericilik Toplusu, 2020, № 9, maddələr 1209, 1231, № 10, maddələr 1274, 1275, 1278, 1283, 1298, 1314, 1315, № 11, maddələr 1387, 1394; Azərbaycan Respublikası Nazirlər Kabinetinin 2020-ci il 5 dekabr tarixli 484 n&amp;ouml;mrəli, 8 dekabr tarixli 488 n&amp;ouml;mrəli, 25 dekabr tarixli 511 n&amp;ouml;mrəli, 2021-ci il 16 yanvar tarixli 9 n&amp;ouml;mrəli və 29 yanvar tarixli 23 n&amp;ouml;mrəli qərarları) 1.1.15-ci yarımbəndində &amp;ldquo;8 mart&amp;rdquo; s&amp;ouml;zləri &amp;ldquo;9 mart&amp;rdquo; s&amp;ouml;zləri ilə əvəz edilsin.&lt;br /&gt;
&lt;br /&gt;
&lt;strong&gt;Əli Əsədov&lt;/strong&gt;&lt;br /&gt;
&lt;strong&gt;Azərbaycan Respublikasının&lt;/strong&gt;&lt;br /&gt;
&lt;strong&gt;Baş naziri Bakı şəhəri,&lt;/strong&gt;&lt;br /&gt;
&lt;strong&gt;3 mart 2021-ci il № 48&lt;/strong&gt;&lt;/p&gt;',
'locale' => 'az',
),
