            'text_field' => '&lt;p&gt;&amp;ldquo;BAKI METROPOLİTENİNDƏN İSTİFADƏ QAYDALARI&amp;rdquo;NIN TƏSDİQ EDİLMƏSİ BARƏDƏ&lt;br /&gt;
